econtenta_callback = function() {
	var oContext = $('.b-article-details')[0];
	var now = new Date();
	var time = $('time', oContext).attr('datetime');
	var datetime = new Date(time);
	var delta = parseInt(now.getTime()/1000) - parseInt(datetime.getTime()/1000);
	if (parseInt(delta/86400) <= 30) {
		window.econtenta.track({ action: "start_view", action_value: { view: true } });
		$(window).on('scroll.swerige', function() {
			if (getScrollPerc() >= 50 || $(document).height() <= $(window).height()){
				window.econtenta.track({ action: "viewing", action_value: { depth: 0.5 } });
			}
			if (getScrollPerc() >= 80 || $(document).height() <= $(window).height()){
				window.econtenta.track({ action: "stop_view", action_value: { depth: 0.8 } });
			}
		});
		$(document).ready(function() {
			$(window).trigger('scroll.swerige');
		});
	}
	function getScrollPerc() {
		var s = $(window).scrollTop(),
			d = $(document).height(),
			c = $(window).height();
		return (s / (d - c)) * 100;
	}
};

